package com.example.mediaplayerproject;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AlertDialog;
import androidx.appcompat.app.AppCompatActivity;
import androidx.fragment.app.FragmentManager;
import androidx.fragment.app.FragmentTransaction;
import androidx.recyclerview.widget.ItemTouchHelper;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.content.DialogInterface;
import android.content.Intent;
import android.os.Bundle;
import android.util.Log;
import android.view.View;
import android.widget.Button;
import android.widget.ImageButton;
import android.widget.ImageView;
import android.widget.Toast;

import java.io.Serializable;
import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

public class MainActivity extends AppCompatActivity implements DialogInterface.OnClickListener {

    ArrayList<Song> songsList = new ArrayList<>();
    Song song1 = new Song("One More Cup Of Coffee","Bob Dylan","3:47","https://www.syntax.org.il/xtra/bob.m4a","data:image/jpeg;base64,");
    Song song2 = new Song("Sara","Bob Dylan","5:31","https://www.syntax.org.il/xtra/bob1.m4a","data:image/jpeg;base64,");
    Song song3 = new Song("The Man In Me","Bob Dylan","3:06","https://www.syntax.org.il/xtra/bob2.mp3","data:image/jpeg;base64,");

    final String passSongList = "SONG_LIST";

    ImageButton playButton;
    Button addSongButton;
    Button viewSongsList;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        Log.d("Lifecycle: ", "MainActivity onCreate");
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        playButton = findViewById(R.id.imageButtonPlay);
        addSongButton = findViewById(R.id.buttonAddSong);
        viewSongsList = findViewById(R.id.buttonViewList);

    }

    @Override
    protected void onStart() {
        super.onStart();

        songsList.add(song1);
        songsList.add(song2);
        songsList.add(song3);
        //////////////////////////////////////////////////////////////////////////////////////////////
       /* songsList.add(song1);
        songsList.add(song2);
        songsList.add(song3);
        songsList.add(song1);
        songsList.add(song2);
        songsList.add(song3);
        songsList.add(song1);
        songsList.add(song2);
        songsList.add(song3);*/
        //////////////////////////////////////////////////////////////////////////////////////////////


    }

    @Override
    protected void onResume() {
        super.onResume();
        playButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                /** start playing and move to song list recycler view*/
             Intent intent = new Intent(MainActivity.this,MusicService.class);
             intent.putExtra(passSongList,(Serializable)songsList);
             intent.putExtra("command","new_instance");
             startService(intent);
            }
        });

        addSongButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                /** option to add song details , link , and picture with camera or through gallery */
                Intent addSongActivityIntent = new Intent(getApplicationContext(), AddSongActivity.class);
                addSongActivityIntent.putExtra(passSongList,(Serializable)songsList);
                startActivity(addSongActivityIntent);

            }
        });

        viewSongsList.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                Intent songsListIntent = new Intent(getApplicationContext(), SongsListActivity.class);
                songsListIntent.putExtra(passSongList,(Serializable)songsList);
                startActivity(songsListIntent);
            }
        });

    }




    @Override
    public void onClick(DialogInterface dialog, int which) {

    }
}